function userLocale(){const e=new URL(window.location.href),n=e.pathname,t=n.split("/");return 0===t.length?"en":t[1]}function initPage(){const e=document.querySelector(".text-container"),n=document.getElementById("read-more-btn");e&&n&&n.addEventListener("click",()=>{e.classList.toggle("expanded"),n.textContent=e.classList.contains("expanded")?"Read less":"Read more"})}function goBack(){""!==document.referrer?history.back():window.location.href="/"}document.addEventListener("DOMContentLoaded",initPage),window.addEventListener("pageshow",e=>{e.persisted&&initPage()});